  border-radius: 0;
  background: #c9302c;
}
.btn {
  padding: 0 0.8em;
  border-radius: 3px;
  border: 0;
  cursor: default;
  display: inline-block;
  color: #231f20;
  background: #ffffff;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 0.5px 1px rgba(0, 0, 0, 0.15);
  height: 1.9em;
  line-height: 1.9em;
  font-size: 13.02px;
}
.btn img.content-mask {
  background-color: #231f20;
}
.btn .text {
  margin-left: 6px;
}
.btn:active {
  cursor: default;
  background: #e7e7e7;
}
.btn:focus {
  outline: none;
}
.btn.btn-small {
  font-size: 12.04px;
}
.btn.btn-large {
  font-size: 14px;
  padding: 0 1.3em;
  line-height: 2.2em;
  height: 2.3em;
}
.btn.btn-larger {
  font-size: 15.96px;
  padding: 0 1.6em;
}
.btn.btn-action {
  color: #231f20;
  background: #5cb346;
}
.btn.btn-action img.content-mask {
  background-color: #231f20;
}
.btn.btn-disabled {
  color: rgba(35, 31, 32, 0.5);
  background: rgba(254, 254, 254, 0.75);
}
.btn.btn-disabled:active {
  background: rgba(254, 254, 254, 0.75);
}
.btn.btn-emphasis {
  position: relative;
  color: #ffffff;
  font-weight: 500;
  background: linear-gradient(to bottom, #6bb1f9 0%, #0a80ff 100%);
  box-shadow: none;
  border: 1px solid #3878fa;
}
.btn.btn-emphasis img.content-mask {
  background-color: #ffffff;
}
.btn.btn-emphasis.btn-disabled {
  opacity: 0.4;
}
.btn.btn-emphasis:before {
  content: ' ';
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  border-radius: 4px;
  top: -1px;
  left: -1px;
  position: absolute;
  z-index: -1;
  background: linear-gradient(to bottom, #4ca2f9 0%, #015cff 100%);
}
.btn.btn-emphasis:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#296efa), to(#4783fb));
}
.btn.btn-danger,
.btn .btn-destructive {
  color: #ffffff;
  background: #d9534f;
}
.btn.btn-danger img.content-mask,
.btn .btn-destructive img.content-mask {
  background-color: #ffffff;
}
.btn-toolbar {
  background: -webkit-gradient(linear, left top, left bottom, from(#fefefe), to(#f1f1f1));
  padding: 0 13px;
}
.btn-toolbar.narrow {
  padding: 0 9px;
}
.btn-toolbar.btn-disabled {
  opacity: 0.6;
}
.btn-gradient {
  background: -webkit-gradient(linear, left top, left bottom, from(#fefefe), to(#f1f1f1));
}
.btn-gradient:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#e7e7e7), to(#dbdbdb));
}
.btn-icon {
  background: transparent;
  border: 0;
  box-shadow: none;
  color: rgba(35, 31, 32, 0.8);
  margin-right: 10px;
  outline: none !important;
  font-size: 20px;
}
.btn-icon img.content-mask {
  background-color: rgba(35, 31, 32, 0.8);
}
.btn-icon:last-child {
  margin-right: 0;
}
.btn-icon.inverse {
  color: #ffffff;
}
.btn-icon.inverse img.content-mask {
  background-color: #ffffff;
}
.btn-icon.inverse:hover {
  color: white;
}
.btn-icon.inverse:active {
  color: #ffffff;
}
.btn-icon.inverse:active img.content-mask {
  background-color: #ffffff;
}
.btn-icon:hover {
  cursor: default;
  color: #419bf9;
  box-shadow: none;
}
.btn-icon:hover img.content-mask {
  background-color: #419bf9;
}
.btn-icon:active {
  color: #3187e1;
  box-shadow: none;
}
.btn-icon:active img.content-mask {
  background-color: #3187e1;
}
select {
  border: 1px solid rgba(230, 230, 230, 0.9);
  color: #231f20;
  background: #ffffff;
}
html,
body {
  width: 100%;
  height: 100%;
  overflow: hidden;
  font-family: 'Nylas-Pro', 'Helvetica', sans-serif;
  font-size: 14px;
  line-height: 1.5;
  -webkit-font-smoothing: antialiased;
  touch-action: manipulation;
}
mailspring-workspace {
  display: block;
  height: 100%;
  overflow: hidden;
  position: relative;
  font-family: 'Nylas-Pro', 'Helvetica', sans-serif;
  background-color: #ffffff;
}
.sheet-container {
  height: 100%;
}
.sheet {
  background-color: #ffffff;
}
.sheet-stack-enter {
  left: 30px;
  opacity: 0;
  transition: all 0.125s ease-out;
}
.sheet-stack-enter.sheet-stack-enter-active {
  left: 0;
  opacity: 1;
}
.sheet-stack-leave {
  left: 0;
  opacity: 1;
  transition: all 0.125s ease-in;
}
.sheet-stack-leave.sheet-stack-leave-active {
  left: 30px;
  opacity: 0;
}
.toolbar-menu-control {
  display: none;
}
.toolbar-window-controls {
  margin-top: 7px;
  margin-left: 7px /*rtl:ignore */;
  order: -1000 /*rtl: 1000*/;
  min-width: 72px;
  width: 72px;
  flex-grow: 0;
  flex-shrink: 0;
}
.toolbar-window-controls:hover button {
  background-position: 0 -12px;
}
.toolbar-window-controls button {
  -webkit-app-region: no-drag;
  padding: 0;
  width: 12px;
  height: 12px;
  margin: 4px;
  float: left /*rtl:left */;
  background-color: transparent;
  background-repeat: no-repeat;
  background-position: 0 0;
  background-size: 12px 48px;
  border: 0;
}
.toolbar-window-controls button:active {
  background-position: 0 -24px;
}
.toolbar-window-controls .close {
  background-image: url('images/application-frame/close@1x.png');
}
.toolbar-window-controls .minimize {
  background-image: url('images/application-frame/minimize@1x.png');
}
.toolbar-window-controls .maximize {
  background-image: url('images/application-frame/fullscreen@1x.png');
}
.toolbar-window-controls.alt-true .maximize {
  background-image: url('images/application-frame/maximize@1x.png');
}
@media (-webkit-min-device-pixel-ratio: 2) {
  .toolbar-window-controls .close {
    background-image: url('images/application-frame/close@2x.png');
  }
  .toolbar-window-controls .minimize {
    background-image: url('images/application-frame/minimize@2x.png');
  }
  .toolbar-window-controls .maximize {
    background-image: url('images/application-frame/fullscreen@2x.png');
  }
  .toolbar-window-controls.alt-true .maximize {
    background-image: url('images/application-frame/maximize@2x.png');
  }
}
body.is-blurred .toolbar-window-controls button {
  background-position: 0 -36px;
}
body.is-blurred .sheet-toolbar-container {
  background-image: linear-gradient(to bottom, #f6f6f6, #f6f6f6);
}
body.is-blurred .sheet-toolbar-container .btn.btn-toolbar {
  background: none;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
body.is-blurred .sheet-toolbar-container .btn.btn-toolbar img {
  opacity: 0.5;
}
body.is-blurred .sheet-toolbar-container .item-container .window-title {
  opacity: 0.5;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(#ececec 0%, #ececec 1.9%, #e9e9e9 2%, #d2d2d2 100%);
  color: #434648;
}
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #bbbbbb;
  width: 100%;
  height: 34px;
  min-height: 34px;
  max-height: 34px;
  z-index: 10;
}
.sheet-toolbar .item-container > * {
  -webkit-app-region: no-drag;
}
.sheet-toolbar .item-spacer {
  -webkit-app-region: drag;
}
.sheet-toolbar .item-compose {
  order: 101;
}
.sheet-toolbar .item-container .window-title {
  position: absolute;
  text-align: center;
  left: 50%;
  transform: translateX(-50%);
  -webkit-app-region: drag;
  line-height: 34px;
}
.sheet-toolbar .item-container .window-title:hover {
  cursor: default;
}
.sheet-toolbar .item-back {
  order: -999;
  flex-grow: 0;
  flex-shrink: 0;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #434648;
}
.sheet-toolbar .item-back .item-back-title {
  cursor: default;
  color: #434648;
  margin: 0;
  font-size: 18.06px;
  font-weight: 400;
  vertical-align: middle;
  display: inline-block;
}
.sheet-toolbar .item-back:active .item-back-title {
  color: #2d2b2c;
}
.sheet-toolbar .item-back:active img.content-mask {
  background-color: #2d2b2c;
}
.sheet-toolbar .btn-toolbar {
  margin-top: 5px;
  margin-left: 10.5px;
  margin-right: 0;
  flex-shrink: 0;
