css:
/* The Onboarding window should never adopt theme styles. This re-assigns UI
variables and resets commonly overridden styles to ensure the onboarding window
always looks good. Previously we tried to make the theme just not load in the
window, but it uses a hot window which makes that difficult now. */
h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'Nylas-Pro', 'Helvetica', sans-serif;
  line-height: 1.1;
  color: #434648;
}
h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small,
h1 .small,
h2 .small,
h3 .small,
h4 .small,
h5 .small,
h6 .small {
  line-height: 1;
}
h1 {
  font-size: 23.94px;
  font-weight: 600;
}
h2 {
  font-size: 23.94px;
  font-weight: 300;
}
h3 {
  font-size: 20.02px;
  font-weight: 300;
}
h4 {
  font-size: 18.06px;
}
h5 {
  font-size: 14px;
}
h6 {
  font-size: 12.04px;
}
h1,
h2,
h3 {
  margin-top: 21px;
  margin-bottom: 10.5px;
}
h1 small,
h2 small,
h3 small,
h1 .small,
h2 .small,
h3 .small {
  font-size: 65%;
}
h4,
h5,
h6 {
  margin-top: 10.5px;
  margin-bottom: 10.5px;
}
h4 small,
h5 small,
h6 small,
h4 .small,
h5 .small,
h6 .small {
  font-size: 75%;
}
.btn {
  padding: 0 0.8em;
  border-radius: 3px;
  border: 0;
  cursor: default;
  display: inline-block;
  color: #231f20;
  background: #ffffff;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 0.5px 1px rgba(0, 0, 0, 0.15);
  height: 1.9em;
  line-height: 1.9em;
  font-size: 13.02px;
}
.btn img.content-mask {
  background-color: #231f20;
}
.btn .text {
  margin-left: 6px;
}
.btn:active {
  cursor: default;
  background: #e7e7e7;
}
.btn:focus {
  outline: none;
}
.btn.btn-small {
  font-size: 12.04px;
}
.btn.btn-large {
  font-size: 14px;
  padding: 0 1.3em;
  line-height: 2.2em;
  height: 2.3em;
}
.btn.btn-larger {
  font-size: 15.96px;
  padding: 0 1.6em;
}
.btn.btn-disabled {
  color: rgba(35, 31, 32, 0.6);
  background: rgba(254, 254, 254, 0.85);
}
.btn.btn-disabled:active {
  background: rgba(254, 254, 254, 0.85);
}
.btn.btn-emphasis {
  position: relative;
  color: #ffffff;
  font-weight: 500;
  background: linear-gradient(to bottom, #6bb1f9 0%, #0a80ff 100%);
  box-shadow: none;
  border: 1px solid #3878fa;
}
.btn.btn-emphasis img.content-mask {
  background-color: #ffffff;
}
.btn.btn-emphasis.btn-disabled {
  opacity: 0.4;
}
.btn.btn-emphasis:before {
  content: ' ';
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  border-radius: 4px;
  top: -1px;
  left: -1px;
  position: absolute;
  z-index: -1;
  background: linear-gradient(to bottom, #4ca2f9 0%, #015cff 100%);
}
.btn.btn-emphasis:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#296efa), to(#4783fb));
}
@keyframes fadein {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
.alpha-fade-enter {
  opacity: 0.01;
  transition: all 0.15s ease-out;
}
.alpha-fade-enter.alpha-fade-enter-active {
  opacity: 1;
}
.alpha-fade-leave {
  opacity: 1;
  transition: all 0.15s ease-in;
}
.alpha-fade-leave.alpha-fade-leave-active {
  opacity: 0.01;
}
.page-frame {
  text-align: center;
  flex: 1;
}
.page-frame .page-container {
  display: flex;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
}
.page-frame .page {
  background-color: #f3f3f3;
  flex: 1;
}
.page-frame h1 {
  font-weight: 100;
  font-size: 40pt;
  margin: 0;
}
.page-frame h2 {
  line-height: 1.3em;
  font-size: 30pt;
  font-weight: 200;
}
.page-frame h4 {
  font-weight: 400;
  font-size: 20pt;
}
.page-frame .logo-container {
  width: 117px;
  height: 117px;
  display: inline-block;
  padding-top: 40px;
  box-sizing: content-box;
}
.page-frame .btn-add-account {
  min-width: 170px;
  margin-left: 10px;
  transition: width 150ms ease-in-out;
}
.page-frame .btn-add-account.spinning img {
  vertical-align: middle;
  margin-right: 5px;
  margin-bottom: 2px;
}
.page-frame .prompt {
  color: #5d5d5d;
  font-size: 1.07em;
  font-weight: 300;
  margin-top: 20px;
  margin-bottom: 14px;
}
.page-frame .close {
  position: fixed;
  z-index: 100;
  top: 1px;
  left: 6px;
}
.page-frame .back {
  position: fixed;
  top: 15px;
  left: 15px;
  padding: 10px;
}
.page-frame .message {
  margin-bottom: 15px;
  max-width: 600px;
  margin: auto;
  white-space: pre-wrap;
}
.page-frame .message.error {
  color: #a33;
  user-select: text;
}
.page-frame .message.error a {
  color: #a33;
}
.page-frame .message.empty {
  color: gray;
}
.page-frame form.settings {
  padding: 0 20px;
  padding-bottom: 20px;
}
.page-frame form.settings span:last-child input {
  margin-bottom: 0;
}
.page-frame select {
  margin-bottom: 8px;
}
.page-frame input {
  display: inline-block;
  width: 100%;
  padding: 7px;
  margin-bottom: 8px;
  background: #fff;
  color: #333;
  text-align: left;
  border: 1px solid #aaa;
}
.page-frame input::-webkit-input-placeholder {
  color: #c6c6c6;
}
.page-frame input[type='checkbox'] {
  width: initial;
  margin-right: 5px;
}
.page-frame input:disabled {
  background: rgba(255, 255, 255, 0.6);
}
.page-frame input.error {
  border: 1px solid #a33;
}
.page-frame label {
  display: inline-block;
  white-space: nowrap;
  width: 100%;
  color: #888;
  text-align: left;
  padding: 3px 0;
}
.page-frame label[for='subscribe-check'] {
  color: black;
  white-space: inherit;
}
.page-frame label.checkbox {
  width: inherit;
}
.page-frame .toggle-advanced {
  display: inline-block;
  width: 100%;
  font-size: 0.94em;
  text-align: right;
  padding: 0;
}
.page.authenticate {
  flex: 1;
  display: flex;
}
.page.authenticate webview {
  display: flex;
  flex: 1;
}
.page.authenticate .webview-loading-spinner {
  position: absolute;
  right: 17px;
  top: 17px;
  opacity: 0;
  transition: opacity 200ms ease-in-out;
  transition-delay: 200ms;
}
.page.authenticate .webview-loading-spinner.loading-true {
  opacity: 1;
}
.page.authenticate .webview-cover {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #f3f3f3;
  opacity: 1;
  transition: opacity 200ms ease-out;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.page.authenticate .webview-cover .message {
  color: #444;
  opacity: 0;
  margin-top: 20px;
  transition: opacity 200ms ease-out;
}
.page.authenticate .webview-cover .try-again {
  opacity: 0;
  transition: opacity 200ms ease-out;
}
.page.authenticate .webview-cover.slow .message,
.page.authenticate .webview-cover.error .message {
  opacity: 1;
  max-width: 400px;
}
.page.authenticate .webview-cover.error .spinner {
  visibility: hidden;
}
.page.authenticate .webview-cover.error .try-again {
  opacity: 1;
}
.page.authenticate .webview-cover.ready {
  pointer-events: none;
  opacity: 0;
}
.page.account-choose h2 {
  margin-top: 90px;
  margin-bottom: 20px;
}
.page.account-choose .provider-list {
  margin: auto;
  width: 570px;
}
.page.account-choose .cloud-sync-note {
  margin-bottom: 20px;
  cursor: default;
  color: rgba(35, 31, 32, 0.5);
}
.page.account-choose .provider-name {
  font-size: 18px;
  font-weight: 300;
  color: rgba(0, 0, 0, 0.7);
}
.page.account-choose .provider {
  text-align: left;
  cursor: default;
  line-height: 63px;
  width: 280px;
  display: inline-block;
  border: 1px solid rgba(35, 31, 32, 0.1);
  border-radius: 4px;
  margin-left: 2.5px;
  margin-right: 2.5px;
  margin-bottom: 5px;
  background: rgba(255, 255, 255, 0.4);
}
.page.account-choose .provider .icon-container {
  width: 50px;
  height: 50px;
  display: inline-block;
  box-sizing: content-box;
  padding: 0 15px 0 20px;
  vertical-align: top;
  zoom: 0.9;
}
.page.account-choose .provider:hover {
  background: rgba(255, 255, 255, 0.7);
}
.page.account-setup form {
  width: 400px;
  padding-top: 20px;
  margin: auto;
}
.page.account-setup .twocol {
  display: flex;
  flex-direction: row;
  width: 700px;
  margin: auto;
  transition: width 400ms ease-in-out;
}
.page.account-setup .twocol.hide-second-column {
  width: 400px;
}
.page.account-setup .twocol.hide-second-column .col:nth-child(2) {
  opacity: 0;
  flex: 0;
  padding: 0;
  flex-shrink: 1;
}
.page.account-setup .col {
  flex: 1;
  padding: 0 20px;
  opacity: 1;
  border-left: 1px solid #ddd;
  overflow: hidden;
  transition: all 400ms ease-in-out;
}
.page.account-setup .col:first-child {
  border-left: none;
}
.page.account-setup .col-heading {
  text-align: left;
  padding-bottom: 10px;
}
.page.account-setup.AccountExchangeSettingsForm .logo-container {
  padding-top: 36px;
}
.page.account-setup.AccountExchangeSettingsForm .twocol {
  padding-top: 10px;
  padding-bottom: 10px;
}
.page.account-setup.AccountIMAPSettingsForm h2 {
  padding-top: 36px;
}
.page.account-setup.AccountIMAPSettingsForm .logo-container {
  display: none;
}
.page.account-setup.AccountIMAPSettingsForm .twocol {
  padding-top: 20px;
  padding-bottom: 20px;
}
.page.account-setup.google .logo-container,
.page.account-setup.AccountOnboardingSuccess .logo-container {
  padding-top: 160px;
}
.page.tutorial {
  display: flex;
  flex-direction: column;
}
.page.tutorial.appeared-false .tutorial-container .left {
  transform: translate3d(-30px, 0, 0);
  opacity: 0;
}
.page.tutorial.appeared-false .tutorial-container .right {
  transform: translate3d(30px, 0, 0);
  opacity: 0;
}
.page.tutorial .tutorial-container {
  background-color: #f9f9f9;
  display: flex;
  flex-direction: row;
  flex: 1;
}
.page.tutorial .tutorial-container .left {
  align-self: center;
  flex: 2;
  opacity: 1;
  transform: translate3d(0, 0, 0);
  transition: all ease-in-out 400ms;
}
.page.tutorial .tutorial-container .left .screenshot {
  width: 523px;
  height: 385px;
  background: url(mailspring://onboarding/assets/app-screenshot@2x.png) top left no-repeat;
  background-size: contain;
  margin: auto;
  position: relative;
}
.page.tutorial .tutorial-container .left .screenshot .overlay {
  position: absolute;
  width: 40px;
  height: 40px;
  border: 2px solid rgba(0, 0, 0, 0.7);
  border-radius: 20px;
  transform: translate3d(-50%, -50%, 0);
  transition: all cubic-bezier(0.65, 0.05, 0.36, 1) 260ms;
}
.page.tutorial .tutorial-container .left .screenshot .overlay .overlay-content {
  transition: all cubic-bezier(0.65, 0.05, 0.36, 1) 260ms;
  transform: translate3d(-67px, -67px, 0) scale(0.21);
  background: url(mailspring://onboarding/assets/app-screenshot@2x.png) top left no-repeat;
  background-position: 10% 20%;
  border-radius: 73px;
  width: 146px;
  height: 146px;
  opacity: 0;
  display: block;
  position: absolute;
}
.page.tutorial .tutorial-container .left .screenshot .overlay.seen {
  border: 2px solid rgba(0, 0, 0, 0.3);
}
.page.tutorial .tutorial-container .left .screenshot .overlay.expanded {
  width: 150px;
  height: 150px;
  border: 2px solid rgba(0, 0, 0, 0.7);
  border-radius: 75px;
  box-shadow: 0 0 15px rgba(38, 115, 209, 0.5);
  z-index: 2;
}
.page.tutorial .tutorial-container .left .screenshot .overlay.expanded .overlay-content {
  transform: scale(1);
  opacity: 1;
}
.page.tutorial .tutorial-container .right {
  flex: 1;
  padding: 30px;
  padding-left: 0;
  opacity: 1;
  transform: translate3d(0, 0, 0);
  transition: all ease-in-out 400ms;
}
.page.tutorial .tutorial-container .right h2 {
  font-size: 28px;
  font-weight: 300;
  text-align: center;
}
.page.tutorial .tutorial-container .right p {
  font-size: 16px;
  line-height: 1.85em;
  text-align: left;
  padding: 10px 0;
  color: #333;
}
.page.welcome {
  display: flex;
  flex-direction: column;
}
.page.welcome .footer {
  background-image: linear-gradient(to right, #6061c7 0%, #4782ad 100%);
}
.page.welcome a {
  color: white;
  border-bottom: 1px solid white;
  text-decoration: none;
  font-weight: 300;
}
.page.welcome a:hover {
  background-color: rgba(255, 255, 255, 0.1);
}
.page.welcome .steps-container {
  position: relative;
  flex: 1;
  background-image: linear-gradient(to right, #696ae8 0%, #4c9ad2 100%);
  color: white;
  overflow: hidden;
}
.page.welcome .hero-text {
  font-size: 34px;
  line-height: 41px;
  font-weight: 200;
  cursor: default;
  -webkit-font-smoothing: subpixel-antialiased;
}
.page.welcome .sub-text {
  font-size: 17px;
  font-weight: 300;
}
.page.welcome img.icons {
  position: absolute;
  top: 0;
  left: 0;
  pointer-events: none;
}
.page.welcome .footer,
.page.tutorial .footer {
  text-align: center;
  background-color: #ececec;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
  box-shadow: 0 1px 1px solid rgba(255, 255, 255, 0.25);
}
.page.welcome .footer .btn-next,
.page.tutorial .footer .btn-next,
.page.welcome .footer .btn-prev,
.page.tutorial .footer .btn-prev {
  min-width: 160px;
  margin: 20px 10px;
}
.page.welcome .footer .btn-continue,
.page.tutorial .footer .btn-continue {
  font-weight: 300;
  margin: 20px 0;
  min-width: 296px;
  line-height: 2.5em;
  height: 2.5em;
}
body.platform-win32 .page.welcome .footer .btn-continue,
body.platform-win32 .page.tutorial .footer .btn-continue {
  color: white;
}
body.platform-win32 .page.welcome .footer .btn-continue:hover,
body.platform-win32 .page.tutorial .footer .btn-continue:hover {
  background-color: rgba(0, 0, 0, 0.3);
}
body.platform-win32 .page-frame .alpha-fade-enter {
  transition: all 0.01s ease-out;
}
body.platform-win32 .page-frame .alpha-fade-leave {
  transition: opacity 0.01s ease-in;
}
.appearance-mode {
  background-color: #f7f9f9;
  border-radius: 10px;
  border: 1px solid #c6c7c7;
  text-align: center;
  flex: 1;
  padding: 9px;
  padding-top: 10px;
  margin: 10px;
  margin-top: 0;
}
.appearance-mode img {
  background-color: #c6c7c7;
}
.appearance-mode div {
  margin-top: 10px;
  text-transform: capitalize;
  cursor: default;
}
.appearance-mode.active {
  border: 1px solid #3187e1;
  color: #3187e1;
}
.appearance-mode.active img {
  background-color: #3187e1;
}
.alternative-auth p {
  color: #434648;
}
.alternative-auth .url-copy-target {
  width: 50%;
  border: 1px solid #c6c7c7;
  margin: 10px;
}
.alternative-auth .copy-to-clipboard {
  display: inline-block;
  cursor: pointer;
}
.alternative-auth .copy-to-clipboard img {
  background-color: #919191;
}
.alternative-auth .copy-to-clipboard img:active {
  background-color: #231f20;
}
.alternative-auth .hidden {
  opacity: 0;
}
.alternative-auth .visible {
  opacity: 1;
  margin-bottom: 0;
}
.alternative-auth .fadein {
  opacity: 1;
  transition: opacity 2s linear;
}
.alternative-auth .fadeout {
  opacity: 0;
  transition: opacity 1s linear;
}
.alternative-auth input {
  margin-top: 0;
}
.page.initial-subscription .features {
  display: flex;
  max-width: 470px;
  margin-top: 20px;
}
.page.initial-subscription .features ul {
  flex: 1;
  margin: 0;
  line-height: 1.9em;
  list-style-type: none;
  padding: 0;
}
.page.initial-subscription .features ul li {
  text-align: left;
}
.page.initial-subscription .pro-feature-ring {
  display: block;
  width: 205px;
  height: 205px;
  position: relative;
}
.page.initial-subscription .pro-feature-ring .price,
.page.initial-subscription .pro-feature-ring .period {
  display: block;
  z-index: 2;
  position: absolute;
  left: 50%;
  transform: translate(-50%, -50%);
}
.page.initial-subscription .pro-feature-ring .price {
  font-size: 50px;
  line-height: 50px;
  top: 40%;
}
.page.initial-subscription .pro-feature-ring .period {
  top: 56%;
}
